from __future__ import absolute_import 
import magnumfe.cpp as cpp
from dolfin import Matrix

__all__ = ["assemble_scalar_product", "assemble_transp_scalar_product"]

def assemble_scalar_product(x, VV, VS):
  B = Matrix()
  cpp.assemble_scalar_product(x, VV, VS, B)
  return B

def assemble_transp_scalar_product(x, VV, VS):
  B = Matrix()
  cpp.assemble_transp_scalar_product(x, VV, VS, B)
  return B
