from __future__ import absolute_import 

from dolfin import *
from . import LLGTerm
from magnumfe import Constants, Timer, Cache

__all__ = ["ExchangeField"]

class ExchangeField(LLGTerm):

  preconditionable = True

  def __init__(self):
    """
    This class represents the micromagnetic exchange field.

    *Required fields*
      m
        Magnetization

    *Required material parameters*
      Aex
        Exchange constant
    """
    self._assemble_cache = Cache("material")

  def form_rhs(self, state, w):
    return self._f(state) * Dx(state.m[i],j) * Dx(w[i],j)

  def form_lhs(self, state, w, dt_v):
    return - self._f(state) * Dx(dt_v[i],j) * Dx(w[i],j)

  def _f(self, state):
    return Constant(-2.0 / state.scale**2 * Constants.gamma / Constants.mu0) \
           * state.material.Aex / state.material.ms

  def field(self, state, x = None, lump = True):
    cache = self._assemble_cache
    if cache.requires_update(state):
      m = TrialFunction(state.VectorFunctionSpace())
      w = TestFunction(state.VectorFunctionSpace())
      cache.A = assemble(self._f(state) * Dx(m[i],j) * Dx(w[i],j) / Constants.gamma * state.dx("magnetic"))

    with Timer("Exchange Field"):
      return super(ExchangeField, self).field(state, x, lump, lambda state: cache.A*state.m.vector())
