from __future__ import absolute_import 

from dolfin import *
from magnumfe.llg_terms import LLGTerm
from magnumfe.constants import Constants

__all__ = ["SpinCurrent"]

class SpinCurrent(LLGTerm):

  def __init__(self):
    """
    This class represents the coupling of the magnetization with a spin polarized current.
    It should be used in combination with the :class:`SpinDiffusion` class.

    *Required material parameters*
      c
        Spin diffusion coupling constant
    """
    pass

  def form_term_rhs(self, state, w):
    return Constant(Constants.gamma / Constants.mu0) * state.material.c * inner(state.s, w)
